changeSet.sql:
ALTER TABLE personnel ADD CONSTRAINT chk_personnel_naturalization_date
CHECK (
    naturalization_date IS NULL OR
    (naturalization_date >= date_of_birth AND naturalization_date >= '1960-01-01')
);

COMMENT ON COLUMN personnel.nationality_type IS 'Mode d''acquisition de la nationalité: ORIGINE (né camerounais) ou NATURALISATION (acquise)';
COMMENT ON COLUMN personnel.naturalization_date IS 'Date de naturalisation si nationalité acquise par naturalisation';
COMMENT ON COLUMN personnel.village_origine IS 'Village d''origine du personnel';
COMMENT ON COLUMN personnel.tribu_origine IS 'Tribu d''origine du personnel';
COMMENT ON COLUMN personnel.father_name IS 'Nom complet du père';
COMMENT ON COLUMN personnel.mother_name IS 'Nom complet de la mère';

rollback.sql:
COMMENT ON COLUMN personnel.nationality_type IS NULL;
COMMENT ON COLUMN personnel.naturalization_date IS NULL;
COMMENT ON COLUMN personnel.village_origine IS NULL;
COMMENT ON COLUMN personnel.tribu_origine IS NULL;
COMMENT ON COLUMN personnel.father_name IS NULL;
COMMENT ON COLUMN personnel.mother_name IS NULL;
